INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16900, 'Aliskiren', 'Spironolactone', 'Moderate', 'Coadministration of aliskiren with potassium-sparing diuretics or potassium salts may increase the risk of hyperkalemia. All drugs inhibiting the renin-angiotensin system (RAS) including aliskiren can increase serum potassium and cause hyperkalemia. Increases in serum potassium to greater than 5.5 mEq/L were infrequent with aliskiren alone (0.9% compared to 0.6% with placebo). However, when used in combination with an ACE inhibitor in a diabetic population, increases in serum potassium were more frequent (5.5%).', 'DDInter', 'Caution is advised if aliskiren is used in combination with potassium-sparing diuretics or potassium salts. Routine monitoring of electrolytes and renal function may be indicated, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, or a risk for dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Caution is advised if aliskiren is used in combination with potassium-sparing diuretics or potassium salts.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16915/', '', 'Sacubitril', '', 1767369485);
