INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16902, 'Aliskiren', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein may increase the plasma concentrations and pharmacologic effects of aliskiren, which is a substrate of both the isoenzyme and efflux transporter. According to the product labeling, plasma levels of aliskiren were increased approximately 80% by the potent CYP450 3A4 and moderate P-gp inhibitor ketoconazole at a dosage of 200 mg twice daily.', 'DDInter', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of aliskiren such as dizziness, lightheadedness, diarrhea, abdominal pain, and gastroesophageal reflux.', 'Metabolism', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16917/', '', 'Hydrochlorothiazide, Valsartan, Irbesartan, Hydrochlorothiazide, Losartan, Olmesartan, Azilsartan medoxomil, Telmisartan, Candesartan, Sacubitril, Eprosartan', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, More', 1767369485);
