INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (17302, 'Hydrochlorothiazide', 'Valdecoxib', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17317/', '', 'Chondroitin sulfate, Glucosamine', 'Bisoprolol, Sacubitril, Nebivolol, Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (17303, 'Hydrochlorothiazide', 'Vardenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17318/', '', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Mirabegron, Yohimbine, Papaverine, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide', 'Sacubitril, Tinidazole, Ascorbic acid, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Probenecid, Tioconazole, Diiodohydroxyquinoline, Oxytetracycline, More', 1767369485);
