INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (17605, 'Allopurinol', 'Pegloticase', 'Major', 'Concomitant use of oral urate-lowering therapy and pegloticase may potentially blunt the rise of serum uric acid levels, which may make it difficult to assess patient risk for anaphylaxis and infusion reactions. The risk for both is higher in patients whose uric acid level increases to above 6 mg/dL, particularly when 2 consecutive levels above 6 mg/dL are observed. Therefore, serum uric acid levels should be monitored prior to infusions of pegloticase, and treatment discontinuation considered if levels increase to above 6 mg/dL.', 'DDInter', 'Oral urate-lowering medications should be discontinued prior to initiation of pegloticase and not used during pegloticase treatment.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17620/', '', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Rasburicase', 'Lesinurad, Febuxostat, Colchicine, Sulfinpyrazone, Rasburicase, Allopurinol', 1767369485);
