INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (17994, 'Crizotinib', 'Metformin', 'Moderate', 'Coadministration with crizotinib may theoretically increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2B6 (e.g., bupropion), P-glycoprotein (P-gp) (e.g., dabigatran), and/or the organic cation transporters 1 and 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism, based on in vitro data, is decreased clearance due to crizotinib-mediated inhibition of CYP450 2B6, P-gp, and/or OCT1 or OCT2.', 'DDInter', 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever crizotinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18009/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (17995, 'Dabrafenib', 'Metformin', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9, including many antidiabetic agents such as nateglinide, repaglinide, linagliptin, saxagliptin, and sulfonylureas.', 'DDInter', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of dabrafenib, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination. Likewise, patients should be observed for hypoglycemia when dabrafenib is withdrawn from their therapeutic regimen.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18010/', '', 'Lixisenatide, Albiglutide, Guar gum, Ertugliflozin, Semaglutide, Empagliflozin, Dulaglutide, Canagliflozin, Dapagliflozin', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 1767369485);
