INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18313, 'Isoniazid', 'Pioglitazone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18328/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Guar gum', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18314, 'Itraconazole', 'Pioglitazone', 'Minor', 'Itraconazole, a potent inhibitor of CYP450 3A4, does not appear to significantly affect the pharmacokinetics of available oral hypoglycemic agents, most of which are primarily metabolized by CYP450 2C8 (pioglitazone, rosiglitazone, repaglinide) or 2C9 (nateglinide, sulfonylureas). In most patients, no change in antidiabetic effect was reported. No specific precautions should be necessary during coadministration of most oral hypoglycemic agents with itraconazole. However, dosage adjustments may be necessary if an interaction is suspected.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18329/', '', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Sitagliptin, Canagliflozin, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, More', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
