INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18519, 'Estradiol', 'Doripenem', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens. Some investigators believe that antimicrobials interfere with the enterohepatic recirculation of estrogens by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating parent estrogen molecules following first-pass metabolism. It is possible that a small number of women may be more susceptible to contraceptive failure and, consequently, are more sensitive to the effects of antimicrobials on estrogen disposition in vivo, but risk factors or genetic predispositions have yet to be identified.', 'DDInter', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. Alternative or additional methods of birth control should be considered during and for at least one week beyond the last dose of short-term antimicrobial therapy, and for at least the initial weeks of long-term antimicrobial therapy when risk may be the greatest.', 'Antagonism', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18534/', '', 'Norethisterone, Progesterone, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, Dienogest, Norgestrel, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18520, 'Estradiol', 'Doxepin', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18535/', '', 'Norethisterone, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Norgestrel, Bazedoxifene, Prasterone, More', 'Maprotiline, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone, Esketamine, Escitalopram, More', 1767369485);
