INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18637, 'Estradiol', 'Pomalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of pomalidomide. Deep venous thrombosis (DVT) and pulmonary embolism (PE) occur in patients with multiple myeloma treated with pomalidomide.', 'DDInter', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide. Prophylactic antithrombotic measures should be considered after assessing underlying risk factors such as health history and concomitant medications. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18652/', '', 'Norethisterone, Megestrol acetate, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Progesterone, Norgestrel, Testosterone, Prasterone, Methyltestosterone, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
