INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18649, 'Estradiol', 'Ritonavir', 'Moderate', 'Coadministration of conjugated estrogens with ritonavir or nelfinavir may lead to decreased plasma concentrations of conjugated estrogens. The proposed mechanism may involve induction of the CYP450 3A4-mediated metabolism of conjugated estrogens by ritonavir and nelfinavir. Increased metabolism of estrogens may lead to decreased efficacy of the hormone replacement therapy. Ritonavir is a known CYP450 3A4 substrate and inhibitor; however, it has also been shown to reduce plasma concentrations of contraceptive hormones via a possible mechanism involving induction of glucuronosyltransferase and/or CYP450 hydroxylation. In addition, along with being a strong CYP450 3A4 inhibitor, nelfinavir has also been shown to possess CYP450 3A4-inducing properties.', 'DDInter', 'Dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever ritonavir or nelfinavir is added to or withdrawn from therapy with conjugated estrogens.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18664/', '', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Velpatasvir, Rilpivirine, Dolutegravir, Brincidofovir, More', 'Megestrol acetate, Progesterone, Chlorotrianisene, Estrone, Diethylstilbestrol, Bazedoxifene, Prasterone, Methyltestosterone, Megestrol acetate, Estetrol, Nafarelin, More', 1767369485);
