INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18703, 'Estradiol', 'Voxelotor', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voxelotor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18718/', '', 'Megestrol acetate, Etynodiol, Levonorgestrel, Progesterone, Norgestrel, Chlorotrianisene, Diethylstilbestrol, Bazedoxifene, Prasterone, Methyltestosterone, Megestrol acetate, More', 'Betibeglogene autotemcel, Streptokinase, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18704, 'Estradiol', 'Warfarin', 'Moderate', 'Concomitant therapy with estrogen-containing drugs may diminish the therapeutic effects of oral anticoagulants. Estrogens can increase the plasma levels of certain clotting factors such as fibrinogen, prothrombin, and factors VII and VIII in a dose-dependent manner, resulting in increased risk of thromboembolism, stroke, and/or myocardial infarction. The risk may be further increased by lifestyle choices such as smoking and lack of exercise.', 'DDInter', 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks. Close clinical and laboratory monitoring are recommended if the combination is prescribed. Patients should be advised to promptly notify their physician if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Antagonism', 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18719/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, More', 'Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Desogestrel, Dienogest, Norgestrel, Bazedoxifene, Prasterone, Ulipristal, Norelgestromin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18705, 'Alpelisib', 'Alosetron', 'Moderate', 'Coadministration with alpelisib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 2C9, such as warfarin. The proposed mechanism, based on in vitro data, is increased clearance due to alpelisib-mediated induction of CYP450 2C9.', 'DDInter', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when alpelisib is prescribed in combination with drugs that are CYP450 2C9 substrates. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever alpelisib is added to or withdrawn from therapy with these drugs. An alternative agent with no or minimal CYP450 2C9-inducing activity may also be considered.', 'Metabolism', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18720/', '', 'Dicyclomine, Mepenzolate, Glycopyrronium, Papaverine, Propantheline, Isometheptene', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18706, 'Amprenavir', 'Alosetron', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of alosetron, which has been shown in vitro to be partially metabolized by the isoenzyme.', 'DDInter', 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, caution is advised during concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should be advised to immediately discontinue alosetron and notify their physician if they experience constipation or signs and symptoms of ischemic colitis such as rectal bleeding, bloody diarrhea, and new or worsening abdominal pain. Alosetron should not be resumed if ischemic colitis is diagnosed. Ischemic colitis and other serious complications such as obstruction, perforation, impaction, and toxic megacolon have resulted in hospitalization, blood transfusion, surgery, and death.', 'Metabolism', 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, caution is advised during concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18721/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', 'Dicyclomine, Mepenzolate, Glycopyrr