INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (18863, 'Irbesartan', 'Alpelisib', 'Moderate', 'Coadministration with alpelisib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 2C9, such as warfarin. The proposed mechanism, based on in vitro data, is increased clearance due to alpelisib-mediated induction of CYP450 2C9.', 'DDInter', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when alpelisib is prescribed in combination with drugs that are CYP450 2C9 substrates. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever alpelisib is added to or withdrawn from therapy with these drugs. An alternative agent with no or minimal CYP450 2C9-inducing activity may also be considered.', 'Metabolism', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18878/', '', 'Valsartan, Olmesartan, Azilsartan medoxomil, Telmisartan, Eprosartan, Amlodipine, Valsartan, Olmesartan, Azilsartan medoxomil, Nebivolol, Telmisartan, More', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, More', 1767369485);
