INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19099, 'Alprazolam', 'Clotrimazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of benzodiazepines that are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the potential for significant interaction, the concomitant use of triazolam with clotrimazole or miconazole mucous membrane preparations should be avoided if possible. The same precaution probably applies also to oral midazolam and high dosages of intravenous midazolam. Caution is advised during coadministration with alprazolam. Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression if these agents are used together. Alternatively, benzodiazepines that are not metabolized by CYP450 3A4 (e.g., lorazepam, oxazepam, temazepam) may be considered in patients requiring treatment with azole antifungal agents.', 'Metabolism', 'Given the potential for significant interaction, the concomitant use of triazolam with clotrimazole or miconazole mucous membrane preparations should be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19114/', '', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Griseofulvin, Econazole, Flucytosine, Tioconazole, Minocycline, Neomycin, Tinidazole, Ascorbic acid, More', 'Clobazam, Meprobamate, Chlordiazepoxide, Halazepam, Buspirone, Clorazepic acid', 1767369485);
