INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19397, 'Alprazolam', 'Tazemetostat', 'Moderate', 'Coadministration with tazemetostat may decrease the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to induction of CYP450 3A4 by tazemetostat.', 'DDInter', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dose adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tazemetostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19412/', '', 'Clobazam, Meprobamate, Diazepam, Lorazepam, Chlordiazepoxide, Halazepam, Clorazepic acid, Hydroxyzine, Oxazepam', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Mitotane, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19398, 'Alprazolam', 'Teduglutide', 'Moderate', 'Coadministration with teduglutide may increase the absorption of oral medications. The mechanism is attributed to teduglutide''s action to improve intestinal absorption.', 'DDInter', 'Monitor patients receiving concomitant oral drugs requiring titration or with a narrow therapeutic index for adverse reactions due to potential increased absorption. The dose of the concomitant drug may need to be reduced.', 'Absorption', 'Monitor patients receiving concomitant oral drugs requiring titration or with a narrow therapeutic index for adverse reactions due to potential increased absorption.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19413/', '', 'Meprobamate, Buspirone, Hydroxyzine', 'Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, Givosiran, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19399, 'Alprazolam', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations of alprazolam. The mechanism involves inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of alprazolam.', 'DDInter', 'Caution is advised if alprazolam is used in combination with boceprevir or telaprevir. The potential for increased pharmacologic effects of alprazolam, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation.', 'Metabolism', 'Caution is advised if alprazolam is used in combination with boceprevir or telaprevir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19414/', '', 'Clobazam, Meprobamate, Lorazepam, Chlordiazepoxide, Halazepam, Hydroxyzine, Oxazepam', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19400, 'Alprazolam', 'Telithromycin', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telithromycin.', 'DDInter', 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telithromycin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19415/', '', 'Clobazam, Meprobamate, Halazepam, Chlordiazepoxide, Oxazepam', 'Lincomycin, Dirithromycin', 1767369485);
