INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19645, 'Ginseng', 'Alteplase', 'Moderate', 'Ginseng may potentiate the effects of anticoagulants, platelet inhibitors and thrombolytic agents, possibly increasing the risk of bleeding. In vitro studies have shown that one or more of the active constituents of ginseng, known as ginsenosides, can decrease platelet aggregation, although it is not known whether these constituents are absorbed or are bioactive in humans. Bleeding complications secondary to antiplatelet activity have not been reported.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, consumption of ginseng supplements and large amounts of ginseng should preferably be avoided during use of coagulation-modifying agents. In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19660/', '', 'Cilostazol, Iloprost, Treprostinil, Desirudin, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa, Ascorbic acid, Cyclosporine, More', '', 1767369485);
