INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19791, 'Altretamine', 'Ofloxacin', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19806/', '', 'Pertuzumab, Olaparib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, Enasidenib, Belzutifan, More', 'Tinidazole, Tetracycline, Cefepime, Fluconazole, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Rifamycin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19792, 'Altretamine', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', 'DDInter', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19807/', '', 'Pertuzumab, Panobinostat, Vismodegib, Olaparib, Enasidenib, Sonidegib, Porfimer sodium, Estramustine, Celecoxib, Aminolevulinic acid, Vorinostat, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19793, 'Altretamine', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19808/', '', 'Pertuzumab, Vismodegib, Sotorasib, Enasidenib, Belzutifan, Ivosidenib, Sonidegib, Mitotane, Porfimer sodium, Aminolevulinic acid, Omacetaxine mepesuccinate, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19794, 'Altretamine', 'Ozanimod', 'Major', 'Coadministration of ozanimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects. Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues. The safety and efficacy of ozanimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', 'DDInter', 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod. When switching from drugs with prolonged immune effects to ozanimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19809/', '', 'Pertuzumab, Vismodegib, Sotorasib, Belzutifan, Mitotane, Porfimer sodium, Estramustine, Celecoxib, Aminolevulinic acid, Tazemetostat, Vorinostat, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19795, 'Altretamine', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', 'DDInter', 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19810/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, Enasidenib, Belzutifan, Mitotane, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19796, 'Altretamine', 'Pegfilgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19811/', '', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Peginterferon beta-1a, Interferon alfacon-1, Interferon beta-1a, Histamine, Aldesleukin, Pegademase, More', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Aflibercept, Anagrelide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19797, 'Altretamine', 'Pentobarbital', 'Moderate', 'According to a rat model, the coadministration with phenobarbital (PB) may decrease the plasma, liver, and tumor concentrations of altretamine, as well as reduce its antitumor activity. The likely mechanism is PB induction of altretamine metabolism via CYP450 isoenzymes, resulting in decreased time of exposure of tumor cells to altretamine and its active metabolites.', 'DDInter', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible. The same precaution may be applicable to other barbiturates because of their potential to induce CYP450 isoenzymes.', 'Metabolism', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19812/', '', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tagraxofusp, Pegaspargase, Enasidenib, Belzutifan, Porfimer sodium, More', 1767369485);
