INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19801, 'Altretamine', 'Primidone', 'Moderate', 'According to a rat model, the coadministration with phenobarbital (PB) may decrease the plasma, liver, and tumor concentrations of altretamine, as well as reduce its antitumor activity. The likely mechanism is PB induction of altretamine metabolism via CYP450 isoenzymes, resulting in decreased time of exposure of tumor cells to altretamine and its active metabolites.', 'DDInter', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible. The same precaution may be applicable to other barbiturates because of their potential to induce CYP450 isoenzymes.', 'Metabolism', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19816/', '', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tagraxofusp, Pegaspargase, Enasidenib, Belzutifan, Porfimer sodium, Aflibercept, More', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, More', 1767369485);
