INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19865, 'Norethisterone', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones. The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19880/', '', 'Nafarelin, Octreotide, Cetrorelix, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin, Megestrol acetate, Estetrol, Estradiol, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19866, 'Atorvastatin', 'Norethisterone', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins. The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19881/', '', 'Nafarelin, Cetrorelix, Pasireotide, Ganirelix, Relugolix, Lanreotide, Gonadorelin, Megestrol acetate, Ulipristal, Estetrol, Megestrol acetate, Megestrol acetate', 'Amlodipine, Ramipril, Lovastatin, Perindopril, Niacin, Ezetimibe, Bempedoic acid, Indapamide, Gemfibrozil, Fenofibric acid, Mipomersen, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19867, 'Ethinylestradiol', 'Bexarotene', 'Major', 'RECOMMENDED: There are no data concerning the potential effect of bexarotene on hormonal contraceptives. Presumably, coadministration with bexarotene may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene. Because use of bexarotene is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment. Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bexarotene.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19882/', '', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Niraparib, Selinexor, More', 'Estetrol, Histrelin, Leuprolide, Bicalutamide, Triptorelin, Goserelin, Bazedoxifene', 1767369485);
