INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19875, 'Ethinylestradiol', 'Cladribine', 'Moderate', 'RECOMMENDED: Coadministration with cladribine may reduce the efficacy of hormonal contraceptives. The mechanism has not been established. There are no clinical data regarding the use of cladribine with hormonal contraceptives.', 'DDInter', 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm. Women should avoid becoming pregnant during treatment with cladribine and for at least 6 months after the last dose in each treatment course. Women using hormonal contraceptives should add a barrier method during cladribine dosing and for at least 4 weeks after the last dose in each treatment cycle. In addition, male patients should take precautions to prevent pregnancy of their partner during cladribine dosing and for at least 6 months after the last dose in each treatment course.', 'Others', 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19890/', '', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Belumosudil, More', 'Megestrol acetate, Estetrol, Ulipristal, Estradiol, Diethylstilbestrol, Histrelin, Megestrol acetate, Leuprolide, Bicalutamide, Triptorelin, Goserelin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19876, 'Clobazam', 'Norethisterone', 'Moderate', 'RECOMMENDED: Coadministration with clobazam, a weak inducer of CYP450 3A4, may decrease the plasma concentrations of contraceptive hormones.', 'DDInter', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with clobazam. Additional, nonhormonal forms of contraception should be used during and for 28 days after discontinuing clobazam treatment to ensure contraceptive reliability. A significant interaction with hormone-releasing intrauterine systems is not anticipated due to their local action. Injectable progestin-only contraceptives are also thought to be unaffected by enzyme-inducing drugs. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with clobazam.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19891/', '', 'Meprobamate, Chlordiazepoxide, Halazepam, Clorazepic acid, Oxazepam', 'Nafarelin, Octreotide, Elagolix, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin, Ulipristal, More', 1767369485);
