INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19897, 'Flibanserin', 'Ethinylestradiol', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin. The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism. Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. Some oral contraceptives are considered weak inhibitors of these isoenzymes.', 'DDInter', 'Caution is advised when flibanserin is used with hormonal contraceptives. Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', 'Caution is advised when flibanserin is used with hormonal contraceptives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19912/', '', 'Megestrol acetate, Estetrol, Ulipristal, Estradiol, Diethylstilbestrol, Histrelin, Megestrol acetate, Leuprolide, Triptorelin, Goserelin, Conjugated estrogens, More', 'Ritodrine, Bremelanotide, Cabergoline, Black cohosh, Bromocriptine, Naproxen, Ibuprofen', 1767369485);
