INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19899, 'Fosamprenavir', 'Norethisterone', 'Major', 'Some hormonal contraceptives may decrease the plasma concentrations of amprenavir from fosamprenavir when it is administered in the absence of low-dose ritonavir as a pharmacokinetic booster. In contrast, fosamprenavir in combination with ritonavir may reduce the plasma levels of certain contraceptive hormones, and there is also an increased risk of transaminase elevations when these agents are used together. The mechanism of interaction has not been described.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.e., nonhormonal) methods of birth control should be considered in patients treated with fosamprenavir.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19914/', '', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin, Megestrol acetate, Estetrol, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (19900, 'Fosaprepitant', 'Norethisterone', 'Moderate', 'RECOMMENDED: Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment. The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant. Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant. Data are not available for single-dosing of aprepitant 40 mg. However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed. Intrauterine systems are unlikely to be significantly affected because of their local action. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19915/', '', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Relugolix, Lanreotide, Gonadorelin, Estetrol', '', 1767369485);
