INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20161, 'Diclofenac (topical)', 'Plicamycin', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20176/', '', 'Fludrocortisone, Indomethacin, Fluocinolone acetonide, Hydrocortisone, Diclofenac, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, More', 'Idarubicin, Valrubicin, Ixabepilone, Mitoxantrone, Bleomycin, Doxorubicin, Mitomycin, Daunorubicin, Dactinomycin, Epirubicin', 1767369485);
