INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20230, 'Amphotericin B', 'Amiodarone', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', 'DDInter', 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20245/', '', 'Ascorbic acid, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, Oxytetracycline, Zonisamide, More', 'Quinidine, Propafenone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20231, 'Amphotericin B', 'Amisulpride', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', 'DDInter', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20246/', '', 'Tinidazole, Ascorb