INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20470, 'Ambrisentan', 'Verteporfin', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', 'DDInter', 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20485/', '', 'Aflibercept, Bevacizumab', 'Riociguat, Macitentan', 1767369485);
