INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (21158, 'Aminocaproic acid', 'Ticagrelor', 'Minor', 'Antifibrinolytic agents (e.g., aminocaproic acid or tranexamic acid) and recombinant factor VIIa may increase hemostasis and antagonize the effects of ticagrelor. They may be more effective in managing clinical bleeding events that occur during ticagrelor therapy compared to platelet infusions or desmopressin. No data exist regarding a hemostatic benefit of platelet transfusions in the presence of ticagrelor; circulating ticagrelor may inhibit transfused platelets. Template bleeding time did not decrease when ticagrelor was coadministered with desmopressin; therefore, desmopressin is unlikely to be effective in managing clinical bleeding events. Ticagrelor should be discontinued if active bleeding occurs, and may be resumed after the bleeding has been controlled and the cause identified.', 'DDInter', '-', 'Antagonism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21173/', '', 'Aprotinin', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Cangrelor, Dipyridamole, Caplacizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (21159, 'Aminocaproic acid', 'Tretinoin', 'Major', 'Coadministration of tretinoin (all-trans retinoic acid) with antifibrinolytic agents may increase the risk of thrombosis. Tretinoin is associated with a risk of both venous and arterial thrombosis during the first month of treatment that may involve any organ system. Cases of fatal thrombotic complications have been reported rarely in patients concomitantly treated with tretinoin and antifibrinolytic agents.', 'DDInter', 'Caution is advised when tretinoin is prescribed in combination with antifibrinolytic agents such as aminocaproic acid, aprotinin, or tranexamic acid. Patients should be advised to seek medical attention immediately if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, hemoptysis, hematuria, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Synergism', 'Caution is advised when tretinoin is prescribed in combination with antifibrinolytic agents such as aminocaproic acid, aprotinin, or tranexamic acid.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21174/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', '', 1767369485);
