INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (21403, 'Amiodarone', 'Clopidogrel', 'Moderate', 'According to some of the manufacturers'' labeling for amiodarone, ineffective inhibition of platelet aggregation has been reported in association with a potential interaction between clopidogrel and amiodarone. Clopidogrel is a prodrug whose bioactivation is mediated in part by CYP450 2C19 and 3A4. An interaction is theoretically possible on the basis that amiodarone is an inhibitor of CYP450 3A4, while its active metabolite, desethylamiodarone, has been shown to inhibit CYP450 2C19 in vitro.', 'DDInter', 'Until more information is available, it may be advisable to closely monitor the therapeutic efficacy of clopidogrel during concomitant treatment with amiodarone.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21418/', '', 'Iloprost, Lepirudin, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, More', 'Tocainide, Dronedarone, Procainamide, Disopyramide, Bretylium, Moricizine, Ibutilide', 1767369485);
