INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (21433, 'Amiodarone', 'Diclofenamide', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', 'DDInter', 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21448/', '', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Ascorbic acid, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, Oxytetracycline, Zonisamide, More', 1767369485);
