INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (21884, 'Amiodarone', 'Voriconazole', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of amiodarone. The proposed mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of amiodarone. The use of amiodarone has been associated with prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. Within the azole class, ketoconazole and itraconazole are considered potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible. Otherwise, caution and close clinical monitoring are recommended, and patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21899/', '', 'Micafungin, Caspofungin, Oteseconazole, Flucytosine, Anidulafungin', 'Tocainide, Moricizine, Bretylium', 1767369485);
