INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22123, 'Amisulpride', 'Pramipexole', 'Major', 'Agents with central antidopaminergic activity such as amisulpride may antagonize the pharmacologic effects of levodopa and other dopamine agonists, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', 'DDInter', 'Coadministration of amisulpride with levodopa or other dopamine agonists should generally be avoided. If concomitant administration is necessary, patients should be monitored for excessive sedation, hypotension, and potentially diminished therapeutic response to both treatments.', 'Antagonism', 'Coadministration of amisulpride with levodopa or other dopamine agonists should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22138/', '', 'Safinamide, Amantadine, Rasagiline, Selegiline', 'Pimavanserin', 1767369485);
