INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22225, 'Amitriptyline', 'Astemizole', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22240/', '', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, More', 'Azelastine, Ketotifen', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22226, 'Amitriptyline', 'Atazanavir', 'Moderate', 'Product labeling for certain protease inhibitors (PIs) such as amprenavir and atazanavir states that serious and/or life-threatening drug interactions may occur with tricyclic antidepressants (TCAs), presumably due to PI inhibition of TCA metabolism via CYP450 3A4. High plasma levels of TCAs could conceivably lead to serious adverse reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. Moreover, many TCAs are thought to be substrates of multiple CYP450 isoenzymes, thus the effects of CYP450 3A4 inhibition alone are unknown. Some TCAs such as desipramine, doxepin, and nortriptyline are also thought to be primarily metabolized by CYP450 2D6, which is not known to be inhibited by most PIs (except ritonavir) to any significant extent.', 'DDInter', 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs. Pharmacologic response and plasma TCA levels should be monitored more closely whenever a PI is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22241/', '', 'Phenelzine, Desipramine, Tranylcypromine, Desvenlafaxine, Maprotiline, Fluoxetine, Isocarboxazid, Oxitriptan, Milnacipran, Vortioxetine, Nortriptyline, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, More', 1767369485);
