INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22292, 'Amitriptyline', 'Cinacalcet', 'Moderate', 'Coadministration with cinacalcet may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by cinacalcet, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', 'DDInter', 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.g., class IC antiarrhythmic agents, phenothiazines, certain beta blockers, and most tricyclic antidepressants). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cinacalcet is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22307/', '', 'Phenelzine, Tranylcypromine, Vilazodone, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran, Bupropion, More', 'Etelcalcetide, Doxercalciferol, Salmon calcitonin, Calcifediol, Paricalcitol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22293, 'Amitriptyline', 'Ciprofloxacin', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22308/', '', 'Phenelzine, Tranylcypromine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Esketamine', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Tetracycline, Polymyxin B, Chloramphenicol, Gentamicin, More', 1767369485);
