INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22304, 'Amitriptyline', 'Clonidine', 'Major', 'Potentially life-threatening elevations in blood pressure have been observed when tricyclic antidepressants (TCAs) and clonidine have been administered concomitantly. The mechanism is postulated to be enhancement of the pressor response by the TCA. Reports are available for clomipramine, desipramine and imipramine.', 'DDInter', 'It is recommended that this combination be avoided. If no alternatives exist and this combination must be used, blood pressure should be monitored closely, especially during the first few weeks of therapy. When clonidine, the TCA, or the combination is discontinued, gradual withdrawal is recommended to prevent abrupt blood pressure fluctuations. Patients should be advised to seek medical help if they develop potential symptoms of a hypertensive crisis such as nausea, vomiting, sweating, flushing, dizziness, stiff neck, headache or palpitations.', 'Synergism', 'It is recommended that this combination be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22319/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Escitalopram', 'Methazolamide, Neostigmine, Pilocarpine, Diclofenamide, Bimatoprost, Travoprost, Brinzolamide, Brimonidine, Levobunolol, Rimegepant, Eptinezumab, More', 1767369485);
