INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22400, 'Amitriptyline', 'Fenfluramine', 'Moderate', 'The coadministration of fenfluramine and tricyclic antidepressants (TCAs) may result in elevated plasma concentrations of the latter. The mechanism is decreased clearance of TCAs due to inhibition of CYP450 enzymatic activities by fenfluramine. In addition, the combination of fenfluramine with a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', 'DDInter', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Close monitoring is recommended for signs and symptoms of TCA toxicity as well as excessive serotonergic activity such as CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia.', 'Metabolism', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22415/', '', 'Viloxazine', 'Naltrexone, Lacosamide', 1767369485);
