INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22539, 'Amitriptyline', 'Miconazole', 'Moderate', 'The coadministration with azole antifungal agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). Elevated TCA levels may result in CNS toxicity, anticholinergic symptoms, and QT interval prolongation. The mechanism is decreased TCA clearance due to azole-related inhibition of the CYP450 3A4 isoenzyme, which is partially responsible for the metabolism of TCAs like amitriptyline, clomipramine, imipramine, and possibly others.', 'DDInter', 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary. Serum TCA levels should be monitored following initiation or discontinuation of azole antifungal therapy. Patients should be advised to notify their caregiver if they experience excessive dry mouth, visual disturbances, urinary retention, dizziness, syncope, fast or irregular heartbeat, constipation, or seizures.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22554/', '', 'Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Caspofungin, More', 1767369485);
