INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22722, 'Amitriptyline', 'Thiothixene', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22737/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22723, 'Amitriptyline', 'Thyroid, porcine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22738/', '', 'Citalopram, Sertraline, Maprotiline, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', '', 1767369485);
