INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22894, 'Amlodipine', 'Dexmedetomidine', 'Moderate', 'Dexmedetomidine has been associated with hypotensive and negative chronotropic effects. Coadministration with other agents causing these effects, especially vasodilators and alpha-2 agonists, may result in additive effects on blood pressure and heart rate.', 'DDInter', 'Caution and monitoring of the patient''s hemodynamic status are recommended during coadministration.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22909/', '', 'Aliskiren, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Fenofibrate, More', 'Suvorexant, Eszopiclone, Ramelteon, Ethchlorvynol, Tasimelteon, Dichloralphenazone, Valerian', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22895, 'Amlodipine', 'Diatrizoate', 'Moderate', 'Coadministration of calcium channel blockers may increase the cardiovascular effects of ionic X-ray contrast media. The mechanism is unknown.', 'DDInter', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension. Use of a nonionic contrast agent, such as iopamidol, may be considered instead of ionic contrast where clinically appropriate.', 'Synergism', 'Patients taking calcium channel blockers should be closely monitored for increased cardiovascular effects of ionic X-ray contrast media, such as hypotension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22910/', '', 'Ioxilan, Iomeprol, Ioversol, Iohexol, Iodixanol, Ethiodized oil, Iopamidol, Metrizamide', 'Aliskiren, Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22896, 'Amlodipine', 'Diclofenac', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22911/', '', 'Bempedoic acid, Ivabradine, Sacubitril, Clevidipine', 'Fluocinolone acetonide, Fluorometholone, Fluocinolone acetonide, Fluorometholone, Chondroitin sulfate, Esomeprazole, Glucosamine, Phenylbutazone, Tolazoline, Dimethyl sulfoxide, Phenylbutazone, More', 1767369485);
