INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23057, 'Amlodipine', 'Pasireotide', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', 'DDInter', 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23072/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, More', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Ganirelix, Relugolix, Gonadorelin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23058, 'Amlodipine', 'Pemigatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro. Increased exposure to pemigatinib may increase the incidence and severity of serious adverse reactions such as abdominal pain, acute kidney injury, cholangitis, cholangitis infective, diarrhea, failure to thrive, hypercalcemia, hyponatremia, pleural effusion, pyrexia, small intestinal obstruction, and urinary tract infection.', 'DDInter', 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance.', 'Metabolism', 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23073/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, More', 'Dacomitinib, Pexidartinib, Vemurafenib, Vandetanib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23059, 'Amlodipine', 'Penbutolol', 'Moderate', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers are used concomitantly with beta blockers, particularly in patients with ventricular or conduction abnormalities. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. The proposed mechanisms include additive slowing in AV conduction, reduced cardiac contractility secondary to beta-blockade, and decreased peripheral vascular resistance secondary to calcium channel blockade. In addition, some calcium channel blockers may inhibit the CYP450 metabolism of hepatically metabolized beta blockers, resulting in increased serum concentrations.', 'DDInter', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'Metabolism, Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23074/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, More', '', 1767369485);
