INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23137, 'Amlodipine', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23152/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, More', 'Ginkgo biloba, Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23138, 'Amlodipine', 'Tacrolimus', 'Moderate', 'Coadministration with amlodipine may increase the blood concentrations of tacrolimus. The exact mechanism of interaction is unknown but may involve inhibition of the CYP450 3A5 metabolism of tacrolimus.', 'DDInter', 'Caution is advised if tacrolimus is administered concomitantly with amlodipine. Tacrolimus blood levels should be monitored frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of amlodipine therapy in patients who are stabilized on their tacrolimus regimen. Patients should be advised to contact their physician if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Metabolism', 'Caution is advised if tacrolimus is administered concomitantly with amlodipine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23153/', '', 'Bempedoic acid, Indapamide, Indapamide, Nebivolol, Sacubitril, Nebivolol, Clevidipine', 'Deoxycholic acid, Pimecrolimus, Glycopyrronium, Aminobenzoic acid, Ivermectin, Caffeine, Abrocitinib, Dupilumab, Tralokinumab, Brimonidine, Povidone-iodine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23139, 'Amlodipine', 'Tadalafil', 'Minor', 'Based on their pharmacology, phosphodiesterase-5 (PDE5) inhibitors may conceivably potentiate the hypotensive effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23154/', '', 'Bempedoic acid, Ivabradine, Sacubitril', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Papaverine, Magnesium hydroxide, Ammonium chloride, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23140, 'Amlodipine', 'Tazemetostat', 'Moderate', 'Coadministration with tazemetostat may decrease the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to induction of CYP450 3A4 by tazemetostat.', 'DDInter', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dose adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tazemetostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23155/', '', 'Hydrochlorothiazide, Aliskiren, Ramipril, Valsartan, Simvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, More', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Niraparib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23141, 'Amlodipine', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23156/', '', 'Hydrochlorothiazide, Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, Tipranavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23142, 'Amlodipine', 'Telithromycin', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', 'DDInter', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23157/', '', 'Indapamide, Bempedoic acid, Perindopril, Moexipril, Perindopril, Bisoprolol, Benazepril, Trandolapril, Captopril, Fosinopril, Indapamide, More', 'Lincomycin, Dirithromycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23143, 'Amlodipine', 'Telotristat ethyl', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23158/', '', 'Hydrochlorothiazide, Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23144, 'Amlodipine', 'Temsirolimus', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with amlodipine may increase the risk. The mechanism of this interaction has not been established. The onset of angioedema may sometimes be delayed up to two months following initiation of therapy. In addition, amlodipine, a weak CYP450 3A4 inhibitor, may increase the plasma concentrations of mTOR inhibitors, which are primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if amlodipine is administered with mTOR inhibitors. Clinicians and patients should be aware of the potential for increased plasma levels as well as the risk of angioedema when mTOR inhibitors are prescribed with amlodipine. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever amlodipine is added to or withdrawn from therapy. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing. Patients should be monitored for development of other adverse effects including hypertension, hyperlipidemia, renal impairment, proteinuria, infections, and various types of malignancies.', 'Metabolism', 'Caution is advised if amlodipine is administered with mTOR inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23159/', '', 'Hydrochlorothiazide, Acetylsalicylic acid, Bempedoic acid, Indapamide, Hydrochlorothiazide, Indapamide, Bisoprolol, Propranolol, Metoprolol, Carvedilol, Nebivolol, More', 'Dacomitinib, Pexidartinib, Vemurafenib, Vandetanib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23145, 'Amlodipine', 'Tetrabenazine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23160/', '', 'Aliskiren, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Fenofibrate, More', 'Dextromethorphan, Valbenazine, Dalfampridine, Riluzole, Inotersen, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
