INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23498, 'Fenofibrate', 'Celecoxib', 'Moderate', 'Coadministration with fenofibrate may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of CYP450 2C19, 2A6, and 2C9 isoenzymes. The proposed mechanism, based on in vitro data, is decreased clearance due to fenofibrate-mediated inhibition of CYP450 2C19, 2A6, and 2C9.', 'DDInter', 'Until more information is available, caution is advised if fenofibrate is used concomitantly with drugs that are substrates of CYP450 2C19, 2A6, or 2C9 isoenzymes, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever fenofibrate is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Until more information is available, caution is advised if fenofibrate is used concomitantly with drugs that are substrates of CYP450 2C19, 2A6, or 2C9 isoenzymes, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23515/', '', 'Codeine, Oliceridine, Oxymorphone, Naloxone, Tapentadol, Butorphanol, Dezocine, Opium, Dextropropoxyphene, Pentazocine, Dihydrocodeine, More', 'Bempedoic acid, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Bempedoic acid, More', 1767369485);
