INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23735, 'Dalteparin', 'Valsartan', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', 'DDInter', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23754/', '', 'Amlodipine, Sacubitril, Hydrochlorothiazide, Nebivolol, Amlodipine, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23736, 'Darolutamide', 'Valsartan', 'Moderate', 'Coadministration with inhibitors of the hepatic uptake transporter OATP 1B1 (e.g., rifampin, cyclosporine, paritaprevir) or the hepatic efflux transporter MRP2 (e.g., ritonavir) may increase the systemic exposure to valsartan, which is a substrate of both transporters.', 'DDInter', 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2. Pharmacologic response and blood pressure should be monitored more closely following the addition, discontinuation, or change of dosage of the transporter inhibitor, and the valsartan dosage adjusted as necessary.', 'Others', 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23755/', '', 'Irbesartan, Losartan, Azilsartan medoxomil, Nebivolol, Telmisartan, Candesartan, Hydrochlorothiazide, Aliskiren, Eprosartan, Ramipril, Lisinopril, More', 'Toremifene, Abarelix, Flutamide, Enzalutamide, Relugolix, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (23737, 'Drospirenone', 'Valsartan', 'Moderate', 'The use of drospirenone may potentiate the risk of hyperkalemia in patients receiving chronic treatment with drugs that can increase serum potassium such as angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium preparations, and miscellaneous agents like cyclosporine, heparin, tacrolimus, and trimethoprim. Drospirenone has antimineralocorticoid activity and may induce hyperkalemia in susceptible patients.', 'DDInter', 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/23756/', '', 'Hydrochlorothiazide, Sacubitril, Aliskiren, Nebivolol, Simvastatin, Lovastatin, Ezetimibe, Pravastatin, Niacin, Bempedoic acid, Indapamide, Fenofibrate', 'Ulipristal, Norelgestromin, Etonogestrel, Megestrol acetate, Norgestimate, Etynodiol, Medroxyprogesterone acetate, Desogestrel, Dienogest, Estetrol, Ethinylestradiol, More', 1767369485);
