INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24872, 'Anagrelide', 'Pimozide', 'Major', 'Pimozide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with pimozide. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of pimozide with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24891/', '', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, More', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24873, 'Anagrelide', 'Pirbuterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24892/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Ephedrine, Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24874, 'Anagrelide', 'Pirfenidone', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of pirfenidone, which is primarily (70% to 80%) metabolized by the isoenzyme. Other CYP450 isoenzymes also contribute to a minor extent, including CYP450 2C9, 2C19, 2D6, and 2E1. Concomitant inhibition of one or more of these isoenzymes in addition to CYP450 1A2 is expected to further increase the magnitude of interaction.', 'DDInter', 'Caution is advised when pirfenidone is used concomitantly with CYP450 1A2 inhibitors. Patients should be closely monitored for adverse reactions such as hepatotoxicity, photosensitivity, rash, nausea, diarrhea, vomiting and dyspepsia, and consideration be given to dosage reduction, brief interruption, or permanent discontinuation of pirfenidone if clinically necessary in accordance with the product labeling. Coadministration of pirfenidone with CYP450 1A2 inhibitors in addition to inhibitors of other CYP450 isoenzymes involved in the metabolism of pirfenidone should be avoided, including significant inhibitors of CYP450 2C9 (e.g., amiodarone, fluconazole, imatinib, gemfibrozil, miconazole), 2C19 (e.g., chloramphenicol, cimetidine, esomeprazole, fluconazole, lansoprazole, omeprazole), and/or 2D6 (e.g., bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, terbinafine).', 'Metabolism', 'Caution is advised when pirfenidone is used concomitantly with CYP450 1A2 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24893/', '', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, Belzutifan, More', 'Golimumab, Cyclos