INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (25337, 'Hyoscyamine', 'Levodopa', 'Moderate', 'Anticholinergic agents may decrease the absorption and oral bioavailability of levodopa. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby increasing the gastric degradation of levodopa and reducing the amount available for absorption in the small intestine.', 'DDInter', 'Although certain anticholinergic agents may be used as adjunctive therapy in Parkinson''s disease, clinicians should recognize their potential to reduce the oral bioavailability of levodopa in some patients. Pharmacologic response to levodopa should be monitored more closely whenever anticholinergic agents are added to or withdrawn from therapy, and the dosages of the drugs adjusted as necessary.', 'Absorption', 'Although certain anticholinergic agents may be used as adjunctive therapy in Parkinson''s disease, clinicians should recognize their potential to reduce the oral bioavailability of levodopa in some patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/25356/', '', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, More', '', 1767369485);
