INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26301, 'Crizotinib', 'Loperamide', 'Moderate', 'The use of higher than recommended dosages of loperamide (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including syncope, cardiac arrest, and arrhythmia related to prolongation of the QT interval. Under such circumstances, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26333/', '', 'Opium, Eluxadoline, Diphenoxylate, Difenoxin', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Afatinib, Duvelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26302, 'Cyclizine', 'Loperamide', 'Moderate', 'Coadministration with drugs that possess significant anticholinergic activity may potentiate the antimotility effect of loperamide.', 'DDInter', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.g., antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide).', 'Synergism', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26334/', '', 'Difenoxin', 'Azelastine, Ketotifen, Trimethobenzamide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26303, 'Cyclobenzaprine', 'Loperamide', 'Moderate', 'Coadministration with drugs that possess significant anticholinergic activity may potentiate the antimotility effect of loperamide.', 'DDInter', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.g., antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide).', 'Synergism', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26335/', '', 'Diphenoxylate, Difenoxin', 'Chlorzoxazone, Chlormezanone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26304, 'Cyclosporine', 'Loperamide', 'Major', 'Coadministration with drugs that inhibit the P-glycoprotein (P-gp) efflux transporter may increase the concentrations of loperamide in plasma and central nervous system (CNS). Inhibition of P-gp in the intestine increases the systemic absorption of loperamide, while inhibition in the blood brain barrier facilitates loperamide entry into the CNS. Opioid and other adverse effects may be enhanced.', 'DDInter', 'Caution is recommended if loperamide is used with potent P-gp inhibitors. Particular caution is advised when drugs that inhibit other pathways of loperamide elimination (CYP450 2C8; CYP450 3A4) are also used, since they may act synergistically with P-gp inhibitors to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy. "', 'Absorption', 'Caution is recommended if loperamide is used with potent P-gp inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26336/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Upadacitinib, More', 'Opium, Diphenoxylate, Difenoxin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26305, 'Cyproheptadine', 'Loperamide', 'Moderate', 'Coadministration with drugs that possess significant anticholinergic activity may potentiate the antimotility effect of loperamide.', 'DDInter', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.g., antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide).', 'Synergism', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26337/', '', 'Azelastine, Ketotifen, Trimethobenzamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26306, 'Daclatasvir', 'Loperamide', 'Moderate', 'Coadministration with daclatasvir may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by daclatasvir.', 'DDInter', 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever daclatasvir is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised when daclatasvir is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26338/', '', 'Opium, Diphenoxylate, Difenoxin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Etravirine, Dolutegravir, Oseltamivir, More', 1767369485);
