INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26314, 'Desmopressin', 'Loperamide', 'Moderate', 'Coadministration with opiates may increase the plasma concentrations and pharmacologic effects of oral desmopressin. The risk of water intoxication and/or hyponatremia may be increased.', 'DDInter', 'Caution is recommended if desmopressin is used in combination with opiates. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek immediate medical attention if they develop early signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, drowsiness, lethargy, weakness, listlessness, headache, confusion, difficulty concentrating, memory impairment, anuria, and weight gain. Early treatment may help prevent progression to seizure, coma, respiratory arrest, and death.', 'Synergism', 'Caution is recommended if desmopressin is used in combination with opiates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26346/', '', 'Terlipressin', 'Eluxadoline, Diphenoxylate, Difenoxin', 1767369485);
