INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26324, 'Donepezil', 'Loperamide', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', 'DDInter', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26356/', '', 'Opium, Eluxadoline, Diphenoxylate, Difenoxin', 'Ginkgo biloba, Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26325, 'Doxepin', 'Loperamide', 'Moderate', 'Coadministration with drugs that possess significant anticholinergic activity may potentiate the antimotility effect of loperamide.', 'DDInter', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.g., antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide).', 'Synergism', 'Until further information is available, loperamide should be used with caution in combination with drugs that possess significant anticholinergic activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26357/', '', 'Diphenoxylate, Difenoxin', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 1767369485);
