INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26342, 'Erythromycin', 'Loperamide', 'Major', 'Coadministration with drugs that enhance the gastrointestinal absorption or inhibit the metabolism of loperamide (e.g., potent CYP450 3A4 or 2C8 inhibitors) may increase the plasma concentrations and adverse effects of loperamide.', 'DDInter', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism. Particular caution is advised when drugs that inhibit CYP450 3A4 (e.g., azole antifungal agents, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, protease inhibitors, telithromycin) and CYP450 2C8 (e.g., gemfibrozil, clopidogrel) are used together with loperamide, or when one or more of these drugs are combined with inhibitors of P-glycoprotein transport (e.g., amiodarone, cyclosporine, diltiazem, dronedarone, quinidine, verapamil), since they may act synergistically to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy.', 'Metabolism', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26374/', '', 'Lincomycin, Dirithromycin, Minocycline, Isotretinoin, Fluorometholone, Tretinoin, Azelaic acid, Tetracycline, Rifamycin, Kanamycin, Oxytetracycline, More', 'Opium, Diphenoxylate, Difenoxin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26343, 'Escitalopram', 'Loperamide', 'Moderate', 'The use of higher than recommended dosages of loperamide (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including syncope, cardiac arrest, and arrhythmia related to prolongation of the QT interval. Under such circumstances, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26375/', '', 'Eluxadoline, Diphenoxylate, Difenoxin', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26344, 'Esketamine', 'Loperamide', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26376/', '', 'Eluxadoline', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 1767369485);
