INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26378, 'Itraconazole', 'Loperamide', 'Major', 'Coadministration with drugs that enhance the gastrointestinal absorption or inhibit the metabolism of loperamide (e.g., potent CYP450 3A4 or 2C8 inhibitors) may increase the plasma concentrations and adverse effects of loperamide.', 'DDInter', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism. Particular caution is advised when drugs that inhibit CYP450 3A4 (e.g., azole antifungal agents, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, protease inhibitors, telithromycin) and CYP450 2C8 (e.g., gemfibrozil, clopidogrel) are used together with loperamide, or when one or more of these drugs are combined with inhibitors of P-glycoprotein transport (e.g., amiodarone, cyclosporine, diltiazem, dronedarone, quinidine, verapamil), since they may act synergistically to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy.', 'Metabolism', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26410/', '', 'Opium, Diphenoxylate, Difenoxin', 'Micafungin, Caspofungin, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26379, 'Ivacaftor', 'Loperamide', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26411/', '', 'Opium, Diphenoxylate, Difenoxin', 'Nitric Oxide, Doxapram, Elexacaftor, Lumacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26380, 'Loperamide', 'Ivosidenib', 'Moderate', 'The use of higher than recommended dosages of loperamide (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including syncope, cardiac arrest, and arrhythmia related to prolongation of the QT interval. Under such circumstances, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26412/', '', 'Morphine, Diphenoxylate, Opium, Eluxadoline, Difenoxin', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26381, 'Loperamide', 'Ketoconazole', 'Major', 'Coadministration with drugs that enhance the gastrointestinal absorption or inhibit the metabolism of loperamide (e.g., potent CYP450 3A4 or 2C8 inhibitors) may increase the plasma concentrations and adverse effects of loperamide.', 'DDInter', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism. Particular caution is advised when drugs that inhibit CYP450 3A4 (e.g., azole antifungal agents, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, protease inhibitors, telithromycin) and CYP450 2C8 (e.g., gemfibrozil, clopidogrel) are used together with loperamide, or when one or more of these drugs are combined with inhibitors of P-glycoprotein transport (e.g., amiodarone, cyclosporine, diltiazem, dronedarone, quinidine, verapamil), since they may act synergistically to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy.', 'Metabolism', 'Caution is recommended if loperamide is used with drugs that enhance its gastrointestinal absorption or inhibit its metabolism.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26413/', '', 'Opium, Diphenoxylate, Difenoxin', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Ciclopirox, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26382, 'Loperamide', 'Lapatinib', 'Major', 'Coadministration with drugs that inhibit the P-glycoprotein (P-gp) efflux transporter may increase the concentrations of loperamide in plasma and central nervous system (CNS). Inhibition of P-gp in the intestine increases the systemic absorption of loperamide, while inhibition in the blood brain barrier facilitates loperamide entry into the CNS. Opioid and other adverse effects may be enhanced.', 'DDInter', 'Caution is recommended if loperamide is used with potent P-gp inhibitors. Particular caution is advised when drugs that inhibit other pathways of loperamide elimination (CYP450 2C8; CYP450 3A4) are also used, since they may act synergistically with P-gp inhibitors to increase loperamide concentrations. Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes. Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy. "', 'Absorption', 'Caution is recommended if loperamide is used with potent P-gp inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26414/', '', 'Opium, Diphenoxylate, Difenoxin', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Afatinib, Duvelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26383, 'Loperamide', 'Lasmiditan', 'Moderate', 'Coadministration with lasmiditan may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters. The proposed mechanism, based on in vitro data, involves decreased clearance due to inhibition of P-gp and BCRP by lasmiditan.', 'DDInter', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible. If coadministration is required, patients should be carefully monitored for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Distribution', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26415/', '', 'Eluxadoline', 'Frovatriptan, Dihydroergotamine, Rimegepant, Eptinezumab, Eletriptan, Ubrogepant, Ergotamine, Naratriptan, Galcanezumab, Atogepant, Erenumab, More', 1767369485);
