INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26719, 'Edoxaban', 'Antithrombin III human', 'Major', 'Concomitant use of edoxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding.', 'DDInter', 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26751/', '', 'Clopidogrel, Cilostazol, Lepirudin, Acenocoumarol, Urokinase, Dipyridamole, Warfarin, Argatroban, Streptokinase, Eptifibatide, Selexipag, More', 'Acenocoumarol, Selexipag, Edoxaban', 1767369485);
