INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26859, 'Ramucirumab', 'Antithrombin Alfa', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', 'DDInter', 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26891/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Amivantamab, Atezolizumab, Pembrolizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26860, 'Rivaroxaban', 'Antithrombin Alfa', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', 'DDInter', 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26892/', '', 'Clopidogrel, Cilostazol, Lepirudin, Acenocoumarol, Urokinase, Dipyridamole, Warfarin, Argatroban, Desirudin, Eptifibatide, Streptokinase, More', '', 1767369485);
