INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (26880, 'Vitamin E', 'Antithrombin Alfa', 'Moderate', 'Vitamin E may potentiate the effects of anticoagulants and platelet inhibitors. Vitamin E is thought to inhibit the oxidation of reduced vitamin K and interfere with the functions of vitamin K-dependent clotting factors. These effects appear to be dose-dependent and greater in individuals with preexisting vitamin K deficiency.', 'DDInter', 'Patients should consult a healthcare provider before taking any nutritional supplements like vitamin E. Close clinical and laboratory observation for hematologic complications may be appropriate when vitamin E supplementation at dosages greater than 400 units/day is initiated in patients stabilized on anticoagulant or antiplatelet therapy. The dose of the anticoagulant or antiplatelet drug may require adjustment during and after treatment with vitamin E. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any nutritional supplements like vitamin E.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26912/', '', '', '', 1767369485);
