INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27399, 'Bupropion', 'Apalutamide', 'Major', 'The use of bupropion is associated with a dose-related risk of seizures. The risk may be further increased when coadministered with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline).', 'DDInter', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Bupropion as well as concomitant medications should be initiated at the lower end of the dosage range and titrated gradually as needed and as tolerated. The maximum recommended dosage for the specific bupropion formulation should not be exceeded. Bupropion should be discontinued and not restarted in patients who experience a seizure during treatment.', 'Synergism', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27452/', '', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Sibutramine, Diethylpropion, Mazindol, Phenelzine, More', 'Darolutamide, Toremifene, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27400, 'Buspirone', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of buspirone, which is primarily metabolized by the isoenzyme. Conversely, discontinuation of an inducer may increase buspirone plasma concentrations and increase the risk of central nervous system depression and other adverse effects.', 'DDInter', 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers. Pharmacologic response to buspirone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buspirone dosage adjusted as necessary.', 'Metabolism', 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27453/', '', 'Meprobamate, Lorazepam, Halazepam, Chlordiazepoxide, Oxazepam', 'Darolutamide, Anastrozole, Fulvestrant, Toremifene, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, Abiraterone, Exemestane', 1767369485);
